DROP TABLE IF EXISTS `alert_record`;
CREATE TABLE `alert_record` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '主键ID',
  `alert_strategy_id` bigint(20) NOT NULL COMMENT '触发的告警策略ID',
