-- ----------------------------
-- Table structure for alert_strategy
-- ----------------------------
DROP TABLE IF EXISTS `alert_strategy`;
CREATE TABLE `alert_strategy` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '主键ID',
  `name` varchar(100) NOT NULL COMMENT '告警策略名称',
  `strategy_status` varchar(20) NOT NULL DEFAULT 'Enabled' COMMENT '策略状态（Enabled-启用、Disable-禁止）',
  `alert_level_id` bigint(20) NOT NULL COMMENT '告警级别ID',
  `expand` int(11) DEFAULT '0' COMMENT '外扩米数',
  `alert_type_id` bigint(20) NOT NULL COMMENT '告警类型ID',
  `alert_rule_id` bigint(20) NOT NULL COMMENT '告警规则ID',
  `cycle` int(11) DEFAULT '-1' COMMENT '统计周期时长（单位分钟，-1表示及时）',
  `handle_type` varchar(20) DEFAULT 'Manual' COMMENT '处理方式（Manual-手动处理）',
  `created_user_id` varchar(50) DEFAULT NULL COMMENT '创建人ID标识',
  `created_user_name` varchar(100) DEFAULT NULL COMMENT '创建人名称',
  `update_user_id` varchar(50) DEFAULT NULL COMMENT '修改人ID标识',
  `update_user_name` varchar(100) DEFAULT NULL COMMENT '修改人名称',
  `created_at` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `updated_at` datetime DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  `status` varchar(20) DEFAULT 'active' COMMENT '状态（active-正常，delete-已删除）',
  PRIMARY KEY (`id`),
  KEY `idx_alert_level_id` (`alert_level_id`),
  KEY `idx_alert_type_id` (`alert_type_id`),
  KEY `idx_alert_rule_id` (`alert_rule_id`),
  KEY `idx_strategy_status` (`strategy_status`),
  KEY `idx_status` (`status`)
) ENGINE=InnoDB AUTO_INCREMENT=6086851580727297 DEFAULT CHARSET=utf8mb4 COMMENT='告警策略表';

