  `status` varchar(20) DEFAULT 'active' COMMENT '状态（active-正常，delete-已删除）',
  PRIMARY KEY (`id`),
  KEY `idx_status` (`status`)
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8mb4 COMMENT='告警规则策略表';

