-- ----------------------------
-- Table structure for vessel_person_relation
-- ----------------------------
DROP TABLE IF EXISTS `vessel_person_relation`;
CREATE TABLE `vessel_person_relation` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '主键ID',
  `vessel_name` varchar(100) DEFAULT NULL COMMENT '船舶名称',
  `vessel_mmsi` varchar(50) NOT NULL COMMENT '船舶识别码',
  `person_account` json DEFAULT NULL COMMENT '人员账号（JSON数组）',
  `created_user_id` varchar(50) DEFAULT NULL COMMENT '创建人ID标识',
  `created_user_name` varchar(100) DEFAULT NULL COMMENT '创建人名称',
  `update_user_id` varchar(50) DEFAULT NULL COMMENT '修改人ID标识',
  `update_user_name` varchar(100) DEFAULT NULL COMMENT '修改人名称',
  `created_at` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `updated_at` datetime DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  `status` varchar(20) DEFAULT 'active' COMMENT '状态（active-正常，delete-已删除）',
  `voyage_record_id` bigint(20) DEFAULT NULL COMMENT '船舶出海记录id',
  `temporary_person_account` json DEFAULT NULL COMMENT '临时出海人员账号（JSON数组）',
  PRIMARY KEY (`id`),
  KEY `idx_vessel_mmsi` (`vessel_mmsi`),
  KEY `idx_status` (`status`)
) ENGINE=InnoDB AUTO_INCREMENT=6141388563091457 DEFAULT CHARSET=utf8mb4 COMMENT='船舶人员关系表';

