-- ----------------------------
-- Table structure for vessel_voyage_record
-- ----------------------------
DROP TABLE IF EXISTS `vessel_voyage_record`;
CREATE TABLE `vessel_voyage_record` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '主键ID',
  `vessel_mmsi` varchar(50) NOT NULL COMMENT '船舶MMSI',
  `vessel_name` varchar(100) DEFAULT NULL COMMENT '船舶名称',
  `source` varchar(50) DEFAULT NULL COMMENT '数据来源',
  `plan_voyage_start` datetime DEFAULT NULL COMMENT '预计出海开始时间',
  `plan_voyage_end` datetime DEFAULT NULL COMMENT '预计出海结束时间',
  `plan_voyage_person` int(11) DEFAULT NULL COMMENT '预计出海人数',
  `plan_status` varchar(50) DEFAULT 'NotStarted' COMMENT '计划状态（NotStarted-未出海、PendingConfirm-出海待确认，InVoyage-出海中、ArrivalPending-到岗待确认、Arrived-已到岗、Sheltering-避风中）',
  `voyage_start` datetime DEFAULT NULL COMMENT '实际出海开始时间',
  `voyage_end` datetime DEFAULT NULL COMMENT '实际出海结束时间',
  `voyage_person` int(11) DEFAULT '0' COMMENT '实际出海人数',
  `onboard_person_count` int(11) DEFAULT '0' COMMENT '在船人数',
  `boarding_person_count` int(11) DEFAULT '0' COMMENT '登船人数',
  `in_wind_time` datetime DEFAULT NULL COMMENT '进入风场时间',
  `end_wind_time` datetime DEFAULT NULL COMMENT '离开风场时间',
  `departure_location` varchar(200) DEFAULT NULL COMMENT '出发地',
  `destination` varchar(200) DEFAULT NULL COMMENT '目的地',
  `remark` varchar(500) DEFAULT NULL COMMENT '备注',
  `created_user_id` varchar(50) DEFAULT NULL COMMENT '创建人ID标识',
  `created_user_name` varchar(100) DEFAULT NULL COMMENT '创建人名称',
  `update_user_id` varchar(50) DEFAULT NULL COMMENT '修改人ID标识',
  `update_user_name` varchar(100) DEFAULT NULL COMMENT '修改人名称',
  `created_at` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `updated_at` datetime DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  `status` varchar(20) DEFAULT 'active' COMMENT '状态（active-正常，delete-已删除）',
  PRIMARY KEY (`id`),
  KEY `idx_vessel_id` (`vessel_mmsi`),
  KEY `idx_plan_status` (`plan_status`),
  KEY `idx_plan_voyage_start` (`plan_voyage_start`),
  KEY `idx_voyage_start` (`voyage_start`),
  KEY `idx_status` (`status`)
) ENGINE=InnoDB AUTO_INCREMENT=6086761666252801 DEFAULT CHARSET=utf8mb4 COMMENT='船舶出海记录表';

