-- ----------------------------
-- Table structure for weather_config
-- ----------------------------
DROP TABLE IF EXISTS `weather_config`;
CREATE TABLE `weather_config` (
  `id` bigint(20) NOT NULL COMMENT '主键ID',
  `config` json NOT NULL COMMENT '显示模式：none-不显示, hourly_minute-逐时逐分气象实况, daily-逐日气象预报, minute-逐分气象实况, hourly-逐时气象实况',
  `created_user_id` varchar(64) DEFAULT NULL COMMENT '创建人ID标识',
  `created_user_name` varchar(128) DEFAULT NULL COMMENT '创建人名称',
  `update_user_id` varchar(64) DEFAULT NULL COMMENT '修改人ID标识',
  `update_user_name` varchar(128) DEFAULT NULL COMMENT '修改人名称',
  `created_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `updated_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  `status` varchar(8) NOT NULL DEFAULT 'active' COMMENT '状态：delete-已删除、active-未删除',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='外部宏观预警配置表';

